query.sfwSql:
SELECT A.PERSON_ACCOUNT_ID ,PLAN_PARTICIPATION_STATUS, HISTORY_CHANGE_DATE=@PAYMENTDATE , RETIREMENT_DATE INTO #TEMP FROM SGT_PERSON_ACCOUNT A INNER JOIN ( SELECT PA.PAYEE_ACCOUNT_ID, PA.BENEFIT_BEGIN_DATE, PA.PERSON_ID, PAC.PERSON_ACCOUNT_ID, 'RETR' AS PLAN_PARTICIPATION_STATUS,@PAYMENTDATE AS HISTORY_CHANGE_DATE,PA.RETIREMENT_DATE FROM TEMP_TABLE_FOR_PAYMENT PA INNER JOIN SGT_PAYMENT_HISTORY_HEADER PHH ON PHH.PAYEE_ACCOUNT_ID=PA.PAYEE_ACCOUNT_ID AND ISNULL(PHH.ORG_ID,0) = ISNULL(PA.ORG_ID,0) and ISNULL(PHH.PERSON_ID,0) = ISNULL(PA.PERSON_ID,0) INNER JOIN SGT_PAYEE_BENEFIT_ACCOUNT PBC ON PBC.PAYEE_BENEFIT_ACCOUNT_ID = PA.PAYEE_BENEFIT_ACCOUNT_ID INNER JOIN SGT_PERSON_ACCOUNT PAC ON PAC.PERSON_ACCOUNT_ID = PBC.PERSON_ACCOUNT_ID WHERE PHH.PAYMENT_SCHEDULE_ID=@PAYMENTSCHEDULEID AND PA.BENEFIT_ACCOUNT_TYPE_VALUE IN ('DSBL','RTMT') AND PAC.STATUS_VALUE != 'RETR'
) PAYEE ON A.PERSON_ACCOUNT_ID = PAYEE.PERSON_ACCOUNT_ID
UPDATE SGT_PERSON_ACCOUNT SET STATUS_VALUE=PLAN_PARTICIPATION_STATUS, END_DATE= DATEADD(d,-1,b.RETIREMENT_DATE),--/*vinovin:- (Retirement date - 1) will be more appropriate*/ MODIFIED_BY='OPUS Batch', MODIFIED_DATE = GETDATE(), UPDATE_SEQ = UPDATE_SEQ+1 FROM SGT_PERSON_ACCOUNT A INNER JOIN #TEMP b ON A.PERSON_ACCOUNT_ID = B.PERSON_ACCOUNT_ID
